import React, { useState, Dispatch, SetStateAction} from 'react';
import birthdayCake from './assets/birthdayCake.png';
import birthdayCakeFaded from './assets/birthdayCakeFaded.png';
import crown from './assets/crown.png';
import crownFaded from './assets/crownFaded.png';
import forkAndKnive from './assets/forkAndKnive.png';
import forkAndKniveFaded from './assets/forkAndKniveFaded.png';
import lightningBolt from './assets/lightningBolt.png';
import lightningBoltFaded from './assets/birthdayCake.png';
import ship from './assets/ship.png';
import shipFaded from './assets/shipFaded.png';
import skull from './assets/skull.png';
import skullFaded from './assets/skullFaded.png';
import snorkleGear from './assets/snorkleGear.png';
import snorkleGearFaded from './assets/snorkleGearFaded.png';
import stack from './assets/stack.png';
import stackFaded from './assets/stackFaded.png';
import './App.css';

const birthdayCakeIconsArray: string[]      = [birthdayCake, birthdayCakeFaded];
const crownIconsArray: string[]         = [crown, crownFaded];
const forkAndKniveIconsArray: string[]  = [forkAndKnive, forkAndKniveFaded];
const lightningBoltIconsArray: string[] = [lightningBolt, lightningBoltFaded];
const shipIconsArray: string[]          = [ship, shipFaded];
const skullIconsArray: string[]         = [skull, skullFaded];
const snorkleGearIconsArray: string[]   = [snorkleGear, snorkleGearFaded];
const stackIconsArray: string[]         = [stack, stackFaded];

const buildDetailedIconArray = (iconArray: string[][] ) => {
  const detailedIconArray: Data[] = [];

  iconArray.forEach((icons:string[]) => {
    const icon: string = icons[0];
    const iconFaded: string = icons[1];

    const detailedIconObject: Data = {
      icon,
      iconFaded,
      iconClicked: false,
    };

    detailedIconArray.push(detailedIconObject);
  });

  return detailedIconArray;
};

const talentPathOneIconArray: string[][] = [
  stackIconsArray,
  forkAndKniveIconsArray,
  birthdayCakeIconsArray, 
  crownIconsArray,
];

const talentPathTwoIconArray: string[][] = [
  shipIconsArray,
  snorkleGearIconsArray,
  lightningBoltIconsArray,
  skullIconsArray,
];

type Data  = {
  icon: string,
  iconFaded: string,
  iconClicked: boolean,
}

function App() {
  const [count, setCount] = useState<number>(0);
  const [style, setStyle] = useState<string>('')
  const [talentPathOneDetailedIconArray, setTalentPathOneDetailedIconArray] = useState<Data[]>(buildDetailedIconArray(talentPathOneIconArray));
  const [talentPathTwoDetailedIconArray, setTalentPathTwoDetailedIconArray] = useState<Data[]>(buildDetailedIconArray(talentPathTwoIconArray));

  const handleClick = (event: React.MouseEvent<HTMLDivElement, MouseEvent>, icon: Data, i: number, detailedIconArray:Data[], setDetailedIconArray:Dispatch<SetStateAction<Data[]>>) => {
    setStyle("")


    console.log(icon)

    icon.iconClicked = true;
    detailedIconArray[i] = icon;
    setDetailedIconArray(detailedIconArray);



    if (count < 6) {
      setCount(count + 1)
    }
    else {
      console.log("You are out of runes! If you want to change your skills, right click arleady used runes to remove them. Once removed, you will be able to add other skills")
    }
  };

  const handleRightClick = (event: React.MouseEvent<HTMLDivElement, MouseEvent>) => {
    event.preventDefault();
    if (count > 0) {
      setCount(count - 1)
    }
    setStyle('')
  }


  return (
    <div className="App">
      <header className="App-header">
        <div>
          <p> Talent Path 1</p>
          {talentPathOneDetailedIconArray.map((icon: Data, i: number) => {
            console.log(icon)
          return (<div key = {i} className={`${talentPathOneDetailedIconArray[i - 1]?.iconClicked? "active" : "inactive"}`} onClick={e => handleClick(e, icon, i, talentPathOneDetailedIconArray, setTalentPathOneDetailedIconArray)} onContextMenu={handleRightClick}>
            <img src={icon.icon} alt=""/>
          </div>)
        })
          }
        </div>
        <div>
          <p> Talent Path 2</p>
          {talentPathOneDetailedIconArray.map((icon: Data, i: number) => {
          return (<div key = {i} className={`${talentPathTwoDetailedIconArray[i - 1]?.iconClicked? "active" : "inactive"}`} onClick={e => handleClick(e, icon, i, talentPathTwoDetailedIconArray, setTalentPathTwoDetailedIconArray)} onContextMenu={handleRightClick}>
            <img src={icon.icon} alt=""/>
          </div>)
        })
          }
        </div>

        <div>
          Talent Points
          {count}/6
        </div>
      </header>
    </div>
  );
}

export default App;
