import React, { useState } from 'react';
import birthdayCake from './assets/birthdayCake.png';
import birthdayCakeFaded from './assets/birthdayCakeFaded.png';
import crown from './assets/crown.png';
import crownFaded from './assets/crownFaded.png';
import forkAndKnive from './assets/forkAndKnive.png';
import forkAndKniveFaded from './assets/forkAndKniveFaded.png';
import lightningBolt from './assets/lightningBolt.png';
import lightningBoltFaded from './assets/birthdayCake.png';
import ship from './assets/ship.png';
import shipFaded from './assets/shipFaded.png';
import skull from './assets/skull.png';
import skullFaded from './assets/skullFaded.png';
import snorkleGear from './assets/snorkleGear.png';
import snorkleGearFaded from './assets/snorkleGearFaded.png';
import stack from './assets/stack.png';
import stackFaded from './assets/stackFaded.png';
import './App.css';

const birthdayIconsArray: string[]      = [birthdayCake, birthdayCakeFaded];
const crownIconsArray: string[]         = [crown, crownFaded];
const forkAndKniveIconsArray: string[]  = [forkAndKnive, forkAndKniveFaded];
const lightningBoltIconsArray: string[] = [lightningBolt, lightningBoltFaded];
const shipIconsArray: string[]          = [ship, shipFaded];
const skullIconsArray: string[]         = [skull, skullFaded];
const snorkleGearIconsArray: string[]   = [snorkleGear, snorkleGearFaded];
const stackIconsArray: string[]         = [stack, stackFaded];

const iconArray: string[][] = [
  birthdayIconsArray, 
  crownIconsArray,
  forkAndKniveIconsArray,
  lightningBoltIconsArray,
  shipIconsArray,
  skullIconsArray,
  snorkleGearIconsArray,
  stackIconsArray
];

const detailedIconArray: object[] = []

iconArray.forEach((icons:string[]) => {
  const icon: string = icons[0];
  const iconFaded: string = icons[1];

  const detailedIconObject: object = {
    icon,
    iconFaded,
    iconClicked: false,
  };

  detailedIconArray.push(detailedIconObject);
})

function App() {
  const [count, setCount] = useState<number>(0);
  const [style, setStyle] = useState<string>('')

  const handleClick = (event: React.MouseEvent<HTMLDivElement, MouseEvent>) => {
    console.log(event.target);
    console.log(event.currentTarget);

    setStyle("birthdayCake")

    if (count < 6) {
      setCount(count + 1)
    }
    else {
      console.log("You are out of runes! If you want to change your skills, right click arleady used runes to remove them. Once removed, you will be able to add other skills")
    }
  };

  const handleRightClick = (event: React.MouseEvent<HTMLDivElement, MouseEvent>) => {
    event.preventDefault();
    if (count >= 0) {
      setCount(count - 1)
    }

    console.log("Right Click", event.pageX, event.pageY);
    setStyle('')
  }

  return (
    <div className="App">
      <header className="App-header">
        <div>
          <p> Talent Path 1</p>
          <div>
            <div className={style} onClick={handleClick} onContextMenu={handleRightClick}><img src={birthdayCake} alt="birthdayCake" /></div>
            <div className={style} onClick={handleClick} onContextMenu={handleRightClick}>Box 2</div>
            <div className={style} onClick={handleClick} onContextMenu={handleRightClick}>Box 3</div>
            <div className={style} onClick={handleClick} onContextMenu={handleRightClick}>Box 4</div>
          </div>
        </div>
        <div>
          <p> Talent Path 2</p>
          <div>
            <div className={style} onClick={handleClick} onContextMenu={handleRightClick}>Box 1</div>
            <div className={style} onClick={handleClick} onContextMenu={handleRightClick}>Box 2</div>
            <div className={style} onClick={handleClick} onContextMenu={handleRightClick}>Box 3</div>
            <div className={style} onClick={handleClick} onContextMenu={handleRightClick}>Box 4</div>
          </div>
        </div>

        <div>
          Talent Points
          {count}/6
        </div>
      </header>
    </div>
  );
}

export default App;
