import React, { useState, Dispatch, SetStateAction, } from 'react';
import {
  buildDetailedIconArray,
  DetailedIconArrayObject,
  talentPathOneIconArray,
  talentPathTwoIconArray,
}
from './iconArrayGeneration';

import {

}
from './SkillsList'
import './App.css';


function App() {
  const [count, setCount] = useState<number>(0);
  const [talentPathOneDetailedIconArray, setTalentPathOneDetailedIconArray] = useState<DetailedIconArrayObject[]>(buildDetailedIconArray(talentPathOneIconArray));
  const [talentPathTwoDetailedIconArray, setTalentPathTwoDetailedIconArray] = useState<DetailedIconArrayObject[]>(buildDetailedIconArray(talentPathTwoIconArray));

  const handleClick = (event: React.MouseEvent<HTMLDivElement, MouseEvent>, icon: DetailedIconArrayObject, i: number, detailedIconArray:DetailedIconArrayObject[], setDetailedIconArray:Dispatch<SetStateAction<DetailedIconArrayObject[]>>) => {
    if ( i === 0
      || detailedIconArray[i-1]?.iconClicked){
      icon.iconClicked = true;
      detailedIconArray[i] = icon;
      setDetailedIconArray(detailedIconArray);

      if (count < 6) {
        setCount(count + 1)
      }
      else {
        console.log("You are out of runes! If you want to change your skills, right click arleady used runes to remove them. Once removed, you will be able to add other skills")
      }
    }
  };

  const handleRightClick = (event: React.MouseEvent<HTMLDivElement, MouseEvent>, icon: DetailedIconArrayObject, i: number, detailedIconArray:DetailedIconArrayObject[], setDetailedIconArray:Dispatch<SetStateAction<DetailedIconArrayObject[]>>) => {
    event.preventDefault();
    if (count > 0) {
      setCount(count - 1)
      icon.iconClicked = false;
      detailedIconArray[i] = icon;
      setDetailedIconArray(detailedIconArray);
    }
  }


  return (
    <div className="App">
      <header className="App-header">
        <div>
          <p> Talent Path 1</p>
          {talentPathOneDetailedIconArray.map((icon: DetailedIconArrayObject, i: number) => {
          return (<div key = {i} className={`${icon?.iconClicked? "active" : "inactive"}`} onClick={e => handleClick(e, icon, i, talentPathOneDetailedIconArray, setTalentPathOneDetailedIconArray)} onContextMenu={e => handleRightClick(e, icon, i, talentPathOneDetailedIconArray, setTalentPathOneDetailedIconArray)}  >
            <img onMouseOver={(e) => {e.currentTarget.src = icon.icon}} onMouseOut={(e) => {e.currentTarget.src = icon.iconFaded}} src={icon.iconClicked?icon.icon : icon.iconFaded} alt=""/>
          </div>)
        })
          }
        </div>
        <div>
          <p> Talent Path 2</p>
          {talentPathTwoDetailedIconArray.map((icon: DetailedIconArrayObject, i: number) => {
          return (<div key = {i} className={`${icon?.iconClicked? "active" : "inactive"}`} onClick={e => handleClick(e, icon, i, talentPathTwoDetailedIconArray, setTalentPathTwoDetailedIconArray)} onContextMenu={e => handleRightClick(e, icon, i, talentPathTwoDetailedIconArray, setTalentPathTwoDetailedIconArray)}>
            <img src={ icon.iconClicked?icon.icon : icon.iconFaded} alt=""/>
          </div>)
        })
          }
        </div>

        <div>
          Talent Points
          {count}/6
        </div>
      </header>
    </div>
  );
}

export default App;
